			-- SQL script for TimeX
			-- Step 1: Create USER
			CREATE USER ${user_owner} WITH PASSWORD '${password_owner}';

			-- Step 2: Create database
			CREATE DATABASE ${halias} OWNER timex_user;

-- SQL script for TimeX -- Step 1: Drop tables if they exist DROP TABLE IF EXISTS ProjectLeader; DROP TABLE IF EXISTS Timesheet; DROP TABLE IF EXISTS Project; DROP TABLE IF EXISTS Employee; DROP TABLE IF EXISTS Task;
-- Step 2: Create tables CREATE TABLE Project ( projectId serial , name VARCHAR(255) NOT NULL UNIQUE, comment VARCHAR(255) , status INTEGER NOT NULL, contract VARCHAR(255), CONSTRAINT project_pk PRIMARY KEY(projectId) );
CREATE TABLE Employee ( employeeId UUID NOT NULL UNIQUE, surname VARCHAR(64) NOT NULL, name VARCHAR(32) NOT NULL, mail VARCHAR(64) , chief UUID , post VARCHAR(64) , department VARCHAR(255) , username VARCHAR(64) NOT NULL UNIQUE, password VARCHAR(32) , CONSTRAINT employee_pk PRIMARY KEY(employeeId), CONSTRAINT employee_chief_fk FOREIGN KEY(chief) REFERENCES Employee(employeeId) );
CREATE TABLE Task ( taskId serial , name VARCHAR(255) NOT NULL UNIQUE, comment VARCHAR(255) , status INTEGER NOT NULL, CONSTRAINT task_pk PRIMARY KEY(taskId) );
CREATE TABLE Timesheet ( id serial , projectId integer NOT NULL, taskId integer NOT NULL, employeeId UUID NOT NULL, dateTask date NOT NULL, countTime numeric(7,2) NOT NULL, comment VARCHAR(255) , CONSTRAINT timesheet_pk PRIMARY KEY(id), CONSTRAINT timesheet_projectId_fk FOREIGN KEY(projectId) REFERENCES Project(projectId), CONSTRAINT timesheet_taskId_fk FOREIGN KEY(taskId) REFERENCES Task(taskId) );
CREATE TABLE ProjectLeader ( id serial , projectId integer NOT NULL, employeeId UUID NOT NULL, CONSTRAINT projectleader_pk PRIMARY KEY(id), CONSTRAINT projectleader_projectId_fk FOREIGN KEY(projectId) REFERENCES Project(projectId) );
CREATE TABLE persistent_logins ( username VARCHAR(64) NOT NULL, series VARCHAR(64) NOT NULL, token_VARCHAR(64) NOT NULL, last_used TIMESTAMP NOT NULL, CONSTRAINT series_pk PRIMARY_KEY(series) );

				
